import React, { useState, useEffect } from 'react';
import { FaFacebook, FaTwitter, FaInstagram, FaGithub } from 'react-icons/fa'; // Import React icons
import profileImage from '../assets/p1.jpg';
import g1 from '../assets/g1.jpg';
import g2 from '../assets/g2.jpg';
import g3 from '../assets/g3.jpg';

const Header = () => {
  const images = [g1, g2, g3];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [editableContent, setEditableContent] = useState({
    about: "I am a web developer driven by a deep passion for creating innovative and user-focused digital experiences. My approach to web development is centered around blending creativity with functionality, ensuring every project stands out both visually and technically.",
    services: [
      { title: "Website Design and Development", description: "Creating visually appealing and functional websites." },
      { title: "UI/UX Design", description: "Enhance user experience and retention." },
      { title: "Website Maintenance and Support", description: "Updating and fixing websites post-launch." },
      { title: "Web Hosting and Domain Services", description: "Setting up domains and hosting for websites." },
    ],
  });

  // Handle automatic image rotation every 3 seconds
  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);
    return () => clearInterval(intervalId);
  }, [images.length]);

  const handleEdit = (key, value) => {
    setEditableContent((prev) => ({ ...prev, [key]: value }));
  };

  return (
    <div className="max-w-sm mx-auto bg-white shadow-xl rounded-lg overflow-hidden mt-8 fade-in transform transition duration-500 hover:scale-105">
      {/* Profile Image */}
      <div className="bg-gradient-to-r from-teal-500 to-blue-500 p-4">
        <img
          src={profileImage}
          alt="Profile"
          className="w-24 h-24 rounded-full mx-auto border-4 border-gray-900"
        />
      </div>

      {/* Name */}
      <div className="text-center mt-4">
        <h1 className="text-3xl font-bold text-gray-900">Sakthi Kousik R</h1>
        <p className="text-gray-500 italic">"Design, code, innovate, repeat, inspire."</p>
      </div>

      {/* Call-to-Action */}
      <div className="text-center mt-4">
        <a
          href="#contact"
          className="bg-teal-500 text-white px-6 py-3 rounded-full hover:bg-teal-600 transition duration-300 ease-in-out"
        >
          Get in Touch
        </a>
      </div>

      {/* About Us Section */}
      <div id="about" className="p-6 mt-4">
        <h2 className="text-xl font-semibold text-teal-400 border-b-2 border-teal-500 pb-2">About Me</h2>
        <textarea
          className="mt-4 text-gray-700 w-full border border-gray-300 rounded-md p-2"
          value={editableContent.about}
          onChange={(e) => handleEdit('about', e.target.value)}
        />
      </div>

      {/* Social Media Links */}
      <div className="flex justify-center space-x-6 mt-4">
        <a
          href="https://www.instagram.com/sk_peace__/"
          className="text-pink-500 hover:text-pink-400 transition transform hover:scale-110"
        >
          <FaInstagram size={30} />
        </a>
        <a
          href="https://github.com/SAKTHIKOUSIK"
          className="text-gray-400 hover:text-gray-300 transition transform hover:scale-110"
        >
          <FaGithub size={30} />
        </a>
        <a
          href="https://www.facebook.com/share/18TpHdQutD/"
          className="text-blue-500 hover:text-blue-400 transition transform hover:scale-110"
        >
          <FaFacebook size={30} />
        </a>
        <a
          href="https://x.com/sakthikousik?t=NY_YDHXGqecKCp3IiZSeZQ&s=09"
          className="text-blue-400 hover:text-blue-300 transition transform hover:scale-110"
        >
          <FaTwitter size={30} />
        </a>
      </div>

      {/* Services Section */}
      <div id="services" className="p-6 mt-4">
        <h2 className="text-xl font-semibold text-teal-400 border-b-2 border-teal-500 pb-2">Services</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 mt-6">
          {editableContent.services.map((service, index) => (
            <div
              key={index}
              className="bg-white shadow-lg rounded-lg p-6 transform transition hover:scale-105"
            >
              <input
                type="text"
                className="text-lg font-semibold text-gray-900 border-b border-gray-300 focus:outline-none"
                value={service.title}
                onChange={(e) => {
                  const newServices = [...editableContent.services];
                  newServices[index].title = e.target.value;
                  handleEdit('services', newServices);
                }}
              />
              <textarea
                className="mt-2 text-gray-600 w-full border border-gray-300 rounded-md p-2"
                value={service.description}
                onChange={(e) => {
                  const newServices = [...editableContent.services];
                  newServices[index].description = e.target.value;
                  handleEdit('services', newServices);
                }}
              />
            </div>
          ))}
        </div>
      </div>

      {/* Carousel Section */}
      <div id="gallery" className="p-6 mt-4">
        <h2 className="text-xl font-semibold text-teal-400 border-b-2 border-teal-500 pb-2">Gallery</h2>
        <div className="relative mt-4">
          <img
            src={images[currentIndex]}
            alt="Gallery Image"
            className="w-full max-h-64 object-contain mx-auto transition-all duration-500"
          />
        </div>
        <div className="text-center mt-4">
          <button
            className="bg-teal-500 text-white px-6 py-2 rounded-full hover:bg-teal-600 transition duration-300 ease-in-out"
            onClick={() => setCurrentIndex((currentIndex + 1) % images.length)}
          >
            Next Slide
          </button>
        </div>
      </div>

      {/* Product Section */}
      <div id="products" className="p-6 mt-4">
        <h2 className="text-xl font-semibold text-teal-400 border-b-2 border-teal-500 pb-2">Products</h2>
        {/* Products left as is */}
      </div>

      {/* Contact Section */}
      <div id="contact" className="p-6 mt-4">
        <h2 className="text-xl font-semibold text-teal-400 border-b-2 border-teal-500 pb-2">Contact</h2>
        <p className="mt-4 text-gray-700">
          Phone: <a href="tel:+918838390005" className="text-teal-500 hover:underline">+91 8838390005</a>
        </p>
        <p className="mt-4 text-gray-700">
          Email: <a href="mailto:csesakthikousikr@gmail.com" className="text-teal-500 hover:underline">csesakthikousikr@gmail.com</a>
        </p>
        <div className="mt-4 text-gray-700">
          <p>Address: 2/378, Bharathi Nagar, Bagalur, Hosur, pincode:635124</p>
          <iframe
            src="https://www.google.com/maps/embed?pb=!1m14!1m8!1m3!1d78332.6577327912!2d77.6045964!3d12.9618246!3m2!1i1024!2i768!4f13.1!4m2!3d12.961825!4d77.6196234!5e0!3m2!1sen!2sin!4v1637100693894!5m2!1sen!2sin"
            width="100%"
            height="200"
            style={{ border: 0 }}
            allowFullScreen
            loading="lazy"
            className="rounded-md shadow-lg mt-2"
          ></iframe>
        </div>
      </div>
    </div>
  );
};

export default Header;
