import React, { useState } from "react";
import { FaFacebook, FaTwitter, FaInstagram, FaGithub } from "react-icons/fa";
import g1 from "../assets/g1.jpg";
import g2 from "../assets/g2.jpg";
import g3 from "../assets/g3.jpg";

const Header = () => {
  const images = [g1, g2, g3];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isEditMode, setIsEditMode] = useState(false);

  const [profile, setProfile] = useState({
    name: "Sakthi Kousik R",
    quote: "Design, code, innovate, repeat, inspire.",
    address: "2/378, Bharathi Nagar, Bagalur, Hosur, Pincode: 635124",
    phone: "+91 8838390005",
    email: "csesakthikousikr@gmail.com",
    mapLink:
      "https://www.google.com/maps/embed?pb=!1m14!1m8!1m3!1d78332.6577327912!2d77.6045964!3d12.9618246!3m2!1i1024!2i768!4f13.1!4m2!3d12.961825!4d77.6196234!5e0!3m2!1sen!2sin!4v1637100693894!5m2!1sen!2sin",
  });

  const handleSave = () => {
    setIsEditMode(false);
    // Save changes to backend or local storage here if needed
  };

  return (
    <div className="max-w-sm mx-auto bg-white shadow-xl rounded-lg overflow-hidden mt-8">
      {/* Toggle Edit Mode */}
      <button
        onClick={() => setIsEditMode(!isEditMode)}
        className="bg-teal-500 text-white px-4 py-2 rounded-full m-4"
      >
        {isEditMode ? "Save" : "Edit"}
      </button>

      {/* Profile Section */}
      <div className="text-center p-6">
        {isEditMode ? (
          <input
            type="text"
            value={profile.name}
            onChange={(e) => setProfile({ ...profile, name: e.target.value })}
            className="border-b border-teal-500 text-xl font-bold text-gray-900"
          />
        ) : (
          <h1 className="text-3xl font-bold text-gray-900">{profile.name}</h1>
        )}
        {isEditMode ? (
          <textarea
            value={profile.quote}
            onChange={(e) => setProfile({ ...profile, quote: e.target.value })}
            className="border-b border-teal-500 text-gray-500 italic mt-2"
          />
        ) : (
          <p className="text-gray-500 italic">{profile.quote}</p>
        )}
      </div>

      {/* Address Section */}
      <div className="p-6">
        <h2 className="text-xl font-semibold text-teal-400">Contact</h2>
        {isEditMode ? (
          <>
            <input
              type="text"
              value={profile.phone}
              onChange={(e) => setProfile({ ...profile, phone: e.target.value })}
              className="border-b border-teal-500 mt-2"
            />
            <input
              type="text"
              value={profile.email}
              onChange={(e) => setProfile({ ...profile, email: e.target.value })}
              className="border-b border-teal-500 mt-2"
            />
            <textarea
              value={profile.address}
              onChange={(e) =>
                setProfile({ ...profile, address: e.target.value })
              }
              className="border-b border-teal-500 mt-2 w-full"
            />
            <input
              type="text"
              value={profile.mapLink}
              onChange={(e) =>
                setProfile({ ...profile, mapLink: e.target.value })
              }
              className="border-b border-teal-500 mt-2 w-full"
            />
          </>
        ) : (
          <>
            <p className="mt-4 text-gray-700">Phone: {profile.phone}</p>
            <p className="mt-4 text-gray-700">Email: {profile.email}</p>
            <p className="mt-4 text-gray-700">Address: {profile.address}</p>
            <iframe
              src={profile.mapLink}
              width="100%"
              height="200"
              style={{ border: 0 }}
              allowFullScreen={true}
              loading="lazy"
              className="rounded-md shadow-lg mt-2"
            ></iframe>
          </>
        )}
      </div>
    </div>
  );
};

export default Header;
