import React, { useState, useEffect } from 'react';

const Header = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  // Array of images from the 'src-Components-img' folder
  const images = [
    "src-Components-img/1.jpg",
    "src-Components-img/2.jpg",
    "src-Components-img/3.jpg",
    "src-Components-img/4.jpg"
  ];

  // Handle previous slide
  const handlePrevSlide = () => {
    setCurrentSlide((prev) => (prev === 0 ? images.length - 1 : prev - 1));
  };

  // Handle next slide
  const handleNextSlide = () => {
    setCurrentSlide((prev) => (prev === images.length - 1 ? 0 : prev + 1));
  };

  // Automatic slide transition every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev === images.length - 1 ? 0 : prev + 1));
    }, 3000); // 3000ms = 3 seconds

    // Clean up the interval on component unmount
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="max-w-sm mx-auto bg-white shadow-xl rounded-lg overflow-hidden mt-8 fade-in transform transition duration-500 hover:scale-105">
      {/* Profile Image */}
      <div className="bg-gradient-to-r from-teal-500 to-blue-500 p-4">
        <img src="https://via.placeholder.com/150" alt="Profile" className="w-24 h-24 rounded-full mx-auto border-4 border-gray-900" />
      </div>

      {/* Name */}
      <div className="text-center mt-4">
        <h1 className="text-3xl font-bold text-gray-900">John Doe</h1>
        <p className="text-gray-500 italic">"Crafting digital dreams into reality"</p>
      </div>

      {/* Call-to-Action */}
      <div className="text-center mt-4">
        <a href="#contact" className="bg-teal-500 text-white px-6 py-3 rounded-full hover:bg-teal-600 transition duration-300 ease-in-out">Get in Touch</a>
      </div>

      {/* About Us Section */}
      <div id="about" className="p-6 mt-4">
        <h2 className="text-xl font-semibold text-teal-400 border-b-2 border-teal-500 pb-2">About Me</h2>
        <p className="mt-4 text-gray-700">I am a passionate web developer with over 5 years of experience in creating visually stunning and user-friendly websites. My goal is to deliver high-quality work while ensuring client satisfaction.</p>
      </div>

      {/* Social Media Links */}
      <div className="flex justify-center space-x-6 mt-4">
        <a href="#" className="text-blue-500 hover:text-blue-400 transition transform hover:scale-110">
          <i className="fab fa-facebook fa-2x"></i>
        </a>
        <a href="#" className="text-blue-400 hover:text-blue-300 transition transform hover:scale-110">
          <i className="fab fa-twitter-square fa-2x"></i>
        </a>
        <a href="#" className="text-pink-500 hover:text-pink-400 transition transform hover:scale-110">
          <i className="fab fa-instagram fa-2x"></i>
        </a>
        <a href="#" className="text-gray-400 hover:text-gray-300 transition transform hover:scale-110">
          <i className="fab fa-github fa-2x"></i>
        </a>
      </div>

      {/* Image Gallery Section (Carousel) */}
      <div id="gallery" className="p-6 mt-4">
        <h2 className="text-xl font-semibold text-teal-400 border-b-2 border-teal-500 pb-2">Gallery</h2>
        <div className="relative mt-4">
          {/* Image container */}
          <div className="w-full h-64 overflow-hidden rounded-lg shadow-lg">
            <img
              src={images[currentSlide]}
              alt={`Gallery ${currentSlide + 1}`}
              className="w-full h-full object-cover transition-all duration-500"
            />
          </div>

          {/* Navigation buttons */}
          <button
            onClick={handlePrevSlide}
            className="absolute top-1/2 left-4 transform -translate-y-1/2 text-white text-3xl bg-teal-500 p-2 rounded-full opacity-50 hover:opacity-100 transition duration-300"
          >
            &lt;
          </button>
          <button
            onClick={handleNextSlide}
            className="absolute top-1/2 right-4 transform -translate-y-1/2 text-white text-3xl bg-teal-500 p-2 rounded-full opacity-50 hover:opacity-100 transition duration-300"
          >
            &gt;
          </button>
        </div>

        {/* Navigation dots */}
        <div className="flex justify-center mt-4 space-x-2">
          {images.map((_, index) => (
            <button
              key={index}
              onClick={() => setCurrentSlide(index)}
              className={`w-3 h-3 rounded-full ${currentSlide === index ? "bg-teal-500" : "bg-gray-300"}`}
            ></button>
          ))}
        </div>
      </div>

      {/* Other Sections (Services, Contact, etc.) */}
      {/* -- keep them the same -- */}
    </div>
  );
};

export default Header;
