import React, { useState, useEffect } from 'react';
import { FaFacebook, FaTwitter, FaInstagram, FaGithub } from 'react-icons/fa';
import profileImageDefault from '../assets/p1.jpg';
import g1 from '../assets/g1.jpg';
import g2 from '../assets/g2.jpg';
import g3 from '../assets/g3.jpg';

const Header = () => {
  const images = [g1, g2, g3];
  const [currentIndex, setCurrentIndex] = useState(0);

  // Editable fields
  const [name, setName] = useState("Sakthi Kousik R");
  const [profileImage, setProfileImage] = useState(profileImageDefault);
  const [address, setAddress] = useState("2/378, Bharathi Nagar, Bagalur, Hosur, pincode:635124");
  const [phone, setPhone] = useState("+91 8838390005");
  const [email, setEmail] = useState("csesakthikousikr@gmail.com");
  const [location, setLocation] = useState(
    "https://www.google.com/maps/embed?pb=!1m14!1m8!1m3!1d78332.6577327912!2d77.6045964!3d12.9618246!3m2!1i1024!2i768!4f13.1!4m2!3d12.961825!4d77.6196234!5e0!3m2!1sen!2sin!4v1637100693894!5m2!1sen!2sin"
  );

  const [isEditing, setIsEditing] = useState(false);

  // Handle automatic image rotation every 3 seconds
  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);
    return () => clearInterval(intervalId);
  }, [images.length]);

  const handleSave = () => {
    setIsEditing(false);
  };

  return (
    <div className="max-w-sm mx-auto bg-white shadow-xl rounded-lg overflow-hidden mt-8">
      {/* Profile Image */}
      <div className="bg-gradient-to-r from-teal-500 to-blue-500 p-4">
        <img
          src={profileImage}
          alt="Profile"
          className="w-24 h-24 rounded-full mx-auto border-4 border-gray-900"
        />
      </div>

      {/* Editable Panel */}
      {isEditing ? (
        <div className="p-6">
          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2">Name</label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="w-full border rounded p-2"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2">Profile Image URL</label>
            <input
              type="text"
              value={profileImage}
              onChange={(e) => setProfileImage(e.target.value)}
              className="w-full border rounded p-2"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2">Address</label>
            <textarea
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              className="w-full border rounded p-2"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2">Phone</label>
            <input
              type="text"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              className="w-full border rounded p-2"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2">Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full border rounded p-2"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2">Google Maps Embed URL</label>
            <input
              type="text"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              className="w-full border rounded p-2"
            />
          </div>
          <button
            onClick={handleSave}
            className="bg-teal-500 text-white px-6 py-3 rounded-full hover:bg-teal-600 transition duration-300 ease-in-out"
          >
            Save Changes
          </button>
        </div>
      ) : (
        <>
          {/* Name */}
          <div className="text-center mt-4">
            <h1 className="text-3xl font-bold text-gray-900">{name}</h1>
            <p className="text-gray-500 italic">"Design, code, innovate, repeat, inspire."</p>
          </div>

          {/* Contact Section */}
          <div className="p-6 mt-4">
            <h2 className="text-xl font-semibold text-teal-400 border-b-2 border-teal-500 pb-2">Contact</h2>
            <p className="mt-4 text-gray-700">Phone: {phone}</p>
            <p className="mt-4 text-gray-700">Email: {email}</p>
            <p className="mt-4 text-gray-700">Address: {address}</p>
            <iframe
              src={location}
              width="100%"
              height="200"
              style={{ border: 0 }}
              allowFullScreen="true"
              loading="lazy"
              className="rounded-md shadow-lg mt-2"
            ></iframe>
          </div>

          {/* Edit Button */}
          <div className="text-center mt-4">
            <button
              onClick={() => setIsEditing(true)}
              className="bg-teal-500 text-white px-6 py-3 rounded-full hover:bg-teal-600 transition duration-300 ease-in-out"
            >
              Edit Profile
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default Header;
