import React, { useState, useEffect } from 'react';
import { FaFacebook, FaTwitter, FaInstagram, FaGithub } from 'react-icons/fa'; // Import React icons
import profileImage from '../assets/p1.jpg';
import g1 from '../assets/g1.jpg';
import g2 from '../assets/g2.jpg';
import g3 from '../assets/g3.jpg';

const Header = () => {
  const images = [g1, g2, g3];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isEditing, setIsEditing] = useState(false);
  const [description, setDescription] = useState(
    'I am a web developer driven by a deep passion for creating innovative and user-focused digital experiences. My approach to web development is centered around blending creativity with functionality, ensuring every project stands out both visually and technically.'
  );
  const [tempDescription, setTempDescription] = useState(description);

  // Handle automatic image rotation every 3 seconds
  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);

    return () => clearInterval(intervalId);
  }, [images.length]);

  // Save edited description
  const handleSave = () => {
    setDescription(tempDescription);
    setIsEditing(false);
  };

  return (
    <div className="max-w-sm mx-auto bg-white shadow-xl rounded-lg overflow-hidden mt-8 fade-in transform transition duration-500 hover:scale-105">
      {/* Profile Image */}
      <div className="bg-gradient-to-r from-teal-500 to-blue-500 p-4">
        <img
          src={profileImage}
          alt="Profile"
          className="w-24 h-24 rounded-full mx-auto border-4 border-gray-900"
        />
      </div>

      {/* Name */}
      <div className="text-center mt-4">
        <h1 className="text-3xl font-bold text-gray-900">Sakthi Kousik R</h1>
        <p className="text-gray-500 italic">"Design, code, innovate, repeat, inspire."</p>
      </div>

      {/* Call-to-Action */}
      <div className="text-center mt-4">
        <a
          href="#contact"
          className="bg-teal-500 text-white px-6 py-3 rounded-full hover:bg-teal-600 transition duration-300 ease-in-out"
        >
          Get in Touch
        </a>
      </div>

      {/* About Us Section */}
      <div id="about" className="p-6 mt-4">
        <h2 className="text-xl font-semibold text-teal-400 border-b-2 border-teal-500 pb-2">About Me</h2>
        {isEditing ? (
          <div>
            <textarea
              className="w-full mt-2 p-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500"
              rows="4"
              value={tempDescription}
              onChange={(e) => setTempDescription(e.target.value)}
            ></textarea>
            <div className="flex justify-end mt-2">
              <button
                onClick={handleSave}
                className="bg-teal-500 text-white px-4 py-2 rounded-md hover:bg-teal-600 mr-2"
              >
                Save
              </button>
              <button
                onClick={() => setIsEditing(false)}
                className="bg-gray-400 text-white px-4 py-2 rounded-md hover:bg-gray-500"
              >
                Cancel
              </button>
            </div>
          </div>
        ) : (
          <div>
            <p className="mt-4 text-gray-700">{description}</p>
            <button
              onClick={() => setIsEditing(true)}
              className="text-teal-500 hover:underline mt-2"
            >
              Edit
            </button>
          </div>
        )}
      </div>

      {/* Social Media Links */}
      <div className="flex justify-center space-x-6 mt-4">
        <a
          href="https://www.instagram.com/sk_peace__/"
          className="text-pink-500 hover:text-pink-400 transition transform hover:scale-110"
        >
          <FaInstagram size={30} />
        </a>
        <a
          href="https://github.com/SAKTHIKOUSIK"
          className="text-gray-400 hover:text-gray-300 transition transform hover:scale-110"
        >
          <FaGithub size={30} />
        </a>
        <a
          href="https://www.facebook.com/share/18TpHdQutD/ "
          className="text-blue-500 hover:text-blue-400 transition transform hover:scale-110"
        >
          <FaFacebook size={30} />
        </a>
        <a
          href="https://x.com/sakthikousik?t=NY_YDHXGqecKCp3IiZSeZQ&s=09"
          className="text-blue-400 hover:text-blue-300 transition transform hover:scale-110"
        >
          <FaTwitter size={30} />
        </a>
      </div>

      {/* Google Map Section */}
      <div id="contact" className="p-6 mt-4">
        <h2 className="text-xl font-semibold text-teal-400 border-b-2 border-teal-500 pb-2">Contact</h2>

        {/* Phone */}
        <p className="mt-4 text-gray-700">
          Phone: <a href="tel:+918838390005" className="text-teal-500 hover:underline">+91 8838390005</a>
        </p>

        {/* Email */}
        <p className="mt-4 text-gray-700">
          Email: <a href="mailto:csesakthikousikr@gmail.com" className="text-teal-500 hover:underline">csesakthikousikr@gmail.com</a>
        </p>

        {/* Address and Map */}
        <div className="mt-4 text-gray-700">
          <p>Address: 2/378, Bharathi Nagar, Bagalur, Hosur, Pincode: 635124</p>
          <iframe
            src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d78332.6577327912!2d77.6045964!3d12.9618246!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3bae17d4ad7e6887%3A0x89bda152a5000aa7!2sBagalur%2C%20Tamil%20Nadu%20635124!5e0!3m2!1sen!2sin!4v1637100693894"
            width="100%"
            height="200"
            style={{ border: 0 }}
            allowFullScreen=""
            loading="lazy"
            className="rounded-md shadow-lg mt-2"
          ></iframe>
        </div>
      </div>
    </div>
  );
};

export default Header;
